vector<int> fnd[N];
//
void relax(int id) {
	int lc = id * 2, rc = lc + 1;
	seg[lc] += lazy[id], lazy[lc] += lazy[id];
	seg[rc] += lazy[id], lazy[rc] += lazy[id];
	lazy[id] = 0;
	return;
}
void add(int l, int r, int id = 1, int s = 0, int e = n) {
	if (e <= l || r <= s)
		return;
	if (l <= s && e <= r) {
		seg[id] += val;
		lazy[id] += val;
		return;
	}
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	relax(id);
	add(l, r, lc, s, mid);
	add(l, r, rc, mid, e);
	seg[id] = min(seg[lc], seg[rc]);
//	cout << "s = " << s << " e = " << e << " : " << seg[id] << endl;
}
void upd(int p, int id = 1, int s = 0, int e = n) {
	if (e - s == 1) {
		seg[id] = val;
		lazy[id] = 0;
		return;
	}
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	relax(id);
	if (p < mid)
		upd(p, lc, s, mid);
	else
		upd(p, rc, mid, e);
	seg[id] = min(seg[lc], seg[rc]);
	return;
}
void open(int id = 1, int s = 0, int e = n) {
	if (e - s == 1) {
		cout << "s = " << s << " : " << seg[id] << endl;
		return;
	}
	int mid = (s + e) / 2, lc = id * 2, rc = lc + 1;
	relax(id);
	open(lc, s, mid);
	open(rc, mid, e);
}
