		while(j < i && (X [i] - X [j]) * 2 > T) j++;
		chmin(dp1 [i],dp1 [j - 1] + T);
		chmin(dp1 [i],dp2 [j - 1] + X [i] * 2);
		chmin(dp2 [i],min(dp2 [i - 1],dp1 [i - 1] - X [i] * 2));
	}

	printf("%lld\n",dp1 [N] + E);

	return 0;
}

