void update(ll root, ll l, ll r, ll i, ll val, ll id){
	if (i < l || r < i) return;
	if (l == r){
		st[root][id] = val; return;
	}
	int mid = (l + r) / 2;
	update(root * 2 + 1, l, mid, i, val, id);
	update(root * 2 + 2, mid + 1, r, i, val, id);
	st[root][id] = min(st[root * 2 + 1][id], st[root * 2 + 2][id]);
}
ll query(ll root, ll l, ll r, ll u, ll v, ll id){
	if (v < l || r < u) return 1e12;
	if (u <= l && r <= v) return st[root][id];
	ll mid = (l + r) / 2;
	return min(query(root * 2 + 1, l, mid, u, v, id), query(root * 2 + 2, mid + 1, r, u, v, id));
}
