  vector<ll> x(n + 1, 0);
  for (int i = 1; i <= n; i++) {
    cin >> x[i];
  }

  vector<ll> dp(n + 1, INFLL);
  dp[0] = 0;

  SegmentTree first_case(n);
  SegmentTree second_case(n);

  first_case.update(0, 0 - 2 * x[1] - x[0]);
  second_case.update(0, 0 - x[0]);

  for (int i = 1; i <= n; i++) {
    int k = lower_bound(x.begin(), x.end(), (2 * x[i] - t) / 2) - x.begin();
    if (k > i - 1) {
      k = i - 1;
    }

    // 1) dp[i] = min(dp[i], dp[j] + 3 * x[i] - 2 * x[j + 1] - x[j]);
    // 2) dp[i] = min(dp[i], dp[j] + x[i] + t - x[j]);

//    cout << k << " first: " << first_case.get(0, k) << " second: " << second_case.get(k + 1, i - 1) << endl;
