//    cout << "2) " << second_case.get(k, i - 1) + x[i] + t << endl;

    dp[i] = min(dp[i], first_case.get(0, k - 1) + 3 * x[i]);
    dp[i] = min(dp[i], second_case.get(k, i - 1) + x[i] + t);

    if (i < n) {
      first_case.update(i, dp[i] - 2 * x[i + 1] - x[i]);
      second_case.update(i, dp[i] - x[i]);
