    vector<ll> dp(N+1,inf);
    dp[0] = 0;
    for(int i=1;i<=N;i++){
        dp[i] = dp[i-1]+X[i]-X[i-1]+T;
        for(int j=1;j<=i-1;j++){
            dp[i] = min(dp[i],dp[j-1]+X[j]-X[j-1]+3*(X[i]-X[j])+max(T-2*(X[i]-X[j]),0LL));
        }
    }
    return dp[N]+(E-X[N]);
}

