	if(dp[ind]!=-1)
		return dp[ind];
	dp[ind]=INF;
	for(int A=ind;A<=N;A++)
		dp[ind]=min(dp[ind],pre[ind][A]+(arr[A+1]-arr[A])+cal(A+1));
	return dp[ind];
}

signed main()
{
	ios_base::sync_with_stdio(false);

	for(int A=1;A<=N+100;A++)
		dp[A]=-1;
	cin>>N>>E>>T;
	for(int A=1;A<=N;A++)
		cin>>arr[A];
	arr[N+1]=E;
	for(int A=1;A<=N;A++)
		for(int B=A;B<=N;B++)
		{
			cur=T-2*(arr[B]-arr[A]);
			if(cur<=0)
				pre[A][B]=3*(arr[B]-arr[A]);
			else
				pre[A][B]=T-2*(arr[B]-arr[A])+3*(arr[B]-arr[A]);
			//cout<<A<<" "<<B<<" "<<pre[A][B]<<"\n";
		}
	cout<<cal(1)+arr[1];
	return 0;
}

