int N,E,T;
int X[202020];
ll memo[2200][2200];

ll dpdp(int yet,int cur) {

	if(memo[yet][cur]>=0) return memo[yet][cur];
	ll ret=0;

	if(cur==N-1) {
		ret = max(T-(X[cur]-X[yet]),(X[cur]-X[yet])) + (X[cur]-X[yet]);
	}
	else {
		ret = max(T-(X[cur]-X[yet]),(X[cur]-X[yet])) + X[cur+1]-X[yet]+dpdp(cur+1,cur+1);
		ret = min(ret, X[cur+1]-X[cur]+dpdp(yet,cur+1));
	}

	return memo[yet][cur]=ret;
}

void solve() {
