inline long long gi()
{
	char ch; long long ret = 0,f = 1;
	do ch = getchar(); while (!(ch >= '0'&&ch <= '9')&&ch != '-');
	if (ch == '-') f = -1,ch = getchar();
	do ret = ret*10+ch-'0',ch = getchar(); while (ch >= '0'&&ch <= '9');
	return ret*f;
}

inline void build(long long now,long long l,long long r)
{
	tree1[now] = tree2[now] = inf;
	if (l == r) return;
	long long mid = (l+r) >> 1;
	build(now<<1,l,mid); build(now<<1|1,mid+1,r);
}

inline void modify(ll *tree,long long now,long long l,long long r,long long pos,ll key)
{
	if (l == r) { tree[now] = key; return; }
	long long mid = (l+r)>>1;
	if (pos <= mid) modify(tree,now<<1,l,mid,pos,key);
	else modify(tree,now<<1|1,mid+1,r,pos,key);
	tree[now] = min(tree[now<<1],tree[now<<1|1]);
}

inline ll query(ll *tree,long long now,long long l,long long r,long long ql,long long qr)
{
	if (ql == l&&qr == r) return tree[now];
	long long mid = (l+r)>>1;
	if (qr <= mid) return query(tree,now<<1,l,mid,ql,qr);
	else if (ql > mid) return query(tree,now<<1|1,mid+1,r,ql,qr);
	else return min(query(tree,now<<1,l,mid,ql,mid),query(tree,now<<1|1,mid+1,r,mid+1,qr));
}

