using namespace std;
const int N = 1e5 + 5;
const ll INF = 1e14;

int n, E, T, x[N];
ll dp[N];

ll val[4 * N], lazy[4 * N];
void refresh(int pos, int low, int high)
{
    if(!lazy[pos])
        return;

    val[pos] += lazy[pos];
    if(low < high)
    {
        lazy[pos * 2] += lazy[pos];
        lazy[pos * 2 + 1] += lazy[pos];
    }
    lazy[pos] = 0;
}

void add_interval(int pos, int low, int high, int l, int r, ll d)
{
    refresh(pos, low, high);
    if(l > high || r < low)
        return;
    if(l <= low && r >= high)
    {
        lazy[pos] += d;
        refresh(pos, low, high);
        return;
    }

    int mid = (low + high) / 2;
    add_interval(pos * 2, low, mid, l, r, d);
    add_interval(pos * 2 + 1, mid + 1, high, l, r, d);

    val[pos] = min(val[pos * 2], val[pos * 2 + 1]);
}

void upd_at(int pos, int low, int high, int id, ll x)
{
    refresh(pos, low, high);
    if(low == high)
    {
        val[pos] = x;
        return;
    }

    int mid = (low + high) / 2;
    if(id <= mid)
        upd_at(pos * 2, low, mid, id, x);
    else
        upd_at(pos * 2 + 1, mid + 1, high, id, x);

    val[pos] = min(val[pos * 2], val[pos * 2 + 1]);
}

ll query(int pos, int low, int high, int l, int r)
{
    refresh(pos, low, high);
    if(l > high || r < low)
        return INF;
    if(l <= low && r >= high)
        return val[pos];

    int mid = (low + high) / 2;
    return min(query(pos * 2, low, mid, l, r), query(pos * 2 + 1, mid + 1, high, l, r));
}

int main()
{
    ios_base::sync_with_stdio(false);
    cin.tie(NULL); cout.tie(NULL);

    cin >> n >> E >> T;
    for(int i = 1; i <= n; i++)
        cin >> x[i];

    upd_at(1, 0, n, 0, T);
    for(int i = 1; i <= n; i++)
        upd_at(1, 0, n, i, INF);


    int leftmost_waiting = 0;
    for(int i = 1; i <= n; i++)
    {
        if(leftmost_waiting < i)
            add_interval(1, 0, n, leftmost_waiting, i - 1, 2LL * (x[i - 1] - x[i]));

        while(leftmost_waiting < i)
        {
            if((ll)T + 2LL * x[leftmost_waiting + 1] - 2LL * x[i] <= 0)
            {
                ll current_val = query(1, 0, n, leftmost_waiting, leftmost_waiting);
                current_val -= (ll)T + 2LL * x[leftmost_waiting + 1] - 2LL * x[i];
                upd_at(1, 0, n, leftmost_waiting, current_val);

                leftmost_waiting++;
            }
            else
                break;
        }


        dp[i] = query(1, 0, n, 0, i - 1) + 3LL * x[i];
        ll optimizing = dp[i] - (2LL * x[i + 1] + x[i]) + T + 2LL * (x[i + 1] - x[i]);
        upd_at(1, 0, n, i, optimizing);
    }


    cout << dp[n] + E - x[n] << endl;


    return 0;
}

