ll n, T, E, x[MAXN], dp[MAXN], seg[4 * MAXN], lazy[4 * MAXN];
void upd(int node, int x)
{
	seg[node] += x;
	lazy[node] += x;
	return;
}
void shift(int node)
{
	upd(node << 1, lazy[node]);
	upd(node << 1 | 1, lazy[node]);
	lazy[node] = 0;
}
void add(int node, int st, int en, int l, int r, int x)
{
	if(l >= en || st >= r)
		return;
	if(l <= st && en <= r)
	{
		upd(node, x);
		return;
	}
	int mid = (st + en) >> 1;
	shift(node);
	add(node << 1, st, mid, l, r, x);
	add(node << 1 | 1, mid, en, l, r, x);
	seg[node] = min(seg[node << 1], seg[node << 1 | 1]);
}
ll get(int node, int st, int en, int l, int r)
{
	if(l >= en || st >= r)
		return inf;
	if(l <= st && en <= r)
		return seg[node];
	int mid = (st + en) >> 1;
	shift(node);
	return min(get(node << 1, st, mid, l, r), get(node << 1 | 1, mid, en, l, r));
}
