			add(1, 0, n + 1, ptr, ptr + 1, -x[ptr + 1] - T + 2 * x[i] - x[ptr + 1]), ptr++;
		dp[i] = get(1, 0, n + 1, 0, i) + x[i];
		add(1, 0, n + 1, i, i + 1, dp[i] - x[i] - x[i + 1]);
	}
