        seg1.update(j + 1, dp[j + 1]);
        seg2.update(j + 1, dp[j + 1] - 2 * x[j + 1]);
    }
    // REP(i,n+1) cout << dp[i] << endl;
    ll ans = e + dp[n];
    cout << ans << endl;
    return 0;
}

