typedef pair <int, int> pii;
int dp[N], a[N], b[N], seg[4][N << 2], lazy[4][N << 2], sum[N];

void shift(int id, int c) {
	seg[id][c << 1] += lazy[id][c];
	seg[id][c << 1 | 1] += lazy[id][c];
	lazy[id][c << 1] += lazy[id][c];
	lazy[id][c << 1 | 1] += lazy[id][c];
	lazy[id][c] = 0;
}

void upd(int id, int c, int b, int e, int l, int r, int x) {
	if(r <= b || e <= l)
		return;
	if(l <= b && e <= r) {
		seg[id][c] += x;
		lazy[id][c] += x;
		return;
	}
	int mid = (b + e) >> 1;
	shift(id, c);
	upd(id, c << 1, b, mid, l, r, x), upd(id, c << 1 | 1, mid, e, l, r, x);
	seg[id][c] = min(seg[id][c << 1], seg[id][c << 1 | 1]);
}

int get(int id, int c, int b, int e, int l, int r) {
	if(r <= b || e <= l)
		return OO;
	if(l <= b && e <= r)
		return seg[id][c];
	int mid = (b + e) >> 1;
	shift(id, c);
	return min(get(id, c << 1, b, mid, l, r), get(id, c << 1 | 1, mid, e, l, r));
}

