struct RMQ {
	int n; vector<int>node;
	void init(int N) {
		n = 1;
		while (n < N)n *= 2;
		node.resize(2 * n, inf);
	}

	void update(int x, int a) {
		x += n - 1;
		node[x] = a;
		while (x > 0) {
			x = (x - 1) / 2;
			node[x] = min(node[x * 2 + 1], node[x * 2 + 2]);
		}
	}
	int query(int a, int b, int k, int l, int r) {
		if (r <= a || b <= l)return inf;
		if (a <= l&&r <= b)return node[k];
		int vl = query(a, b, k * 2 + 1, l, (l + r) / 2);
		int vr = query(a, b, k * 2 + 2, (l + r) / 2, r);
		return min(vl, vr);
	}
	int get(int a, int b) {
		if (a > b)return inf;
		return query(a, b, 0, 0, n);
	}
};
RMQ dp1, dp2;
signed main() {
	int n, e, t; cin >> n >> e >> t;
	rep(i, n)cin >> x[i];
	rep(i, n + 1)dp[i] = inf;
	dp1.init(n + 1);
	dp2.init(n + 1);

	x[n] = x[n - 1];
	dp1.update(0, 0);
	dp2.update(0, -2*x[0]);
	rep(i,n){//dp[i+1]
		int lb = -1, ub = i;
		while (ub - lb > 1) {
			int mi = (ub + lb) / 2;
			if (2 * (x[i] - x[mi]) > t)lb = mi;
			else ub = mi;
		}

		//0~lb ub~i
		int X1, X2;
		X1 = dp1.get(ub, i+1) + t;
		X2 = dp2.get(0, ub) + 2 * x[i];
		dp1.update(i+1, min(X1, X2));
		dp2.update(i+1, dp1.get(i+1, i + 2) - 2 * x[i]);
	}
	cout << dp1.get(n, n+1)+e << endl;
}

