void lazy_evaluate(int k){
	dp[k].val+=dp[k].lazy;
	if(k+1<n_){
		dp[k*2+1].lazy+=dp[k].lazy;
		dp[k*2+2].lazy+=dp[k].lazy;
	}
	dp[k].lazy=0;
}

void lazy_update(int k){
	dp[k].val=min(dp[k*2+1].val,dp[k*2+2].val);
}

ll query(int a,int b,int k=0,int l=0,int r=n_){
	lazy_evaluate(k);
	if(r<=a || b<=l)return INF*INF;
	if(a<=l && r<=b)return dp[k].val;
	ll vl=query(a,b,k*2+1,l,(l+r)/2);
	ll vr=query(a,b,k*2+2,(l+r)/2,r);
	lazy_update(k);
	return min(vl,vr);
}

void add(int a,int b,ll v,int k=0,int l=0,int r=n_){
	lazy_evaluate(k);
	if(r<=a || b<=l)return;
	if(a<=l && r<=b){
		dp[k].lazy+=v;
		lazy_evaluate(k);
		return;
	}
	add(a,b,v,k*2+1,l,(l+r)/2);
	add(a,b,v,k*2+2,(l+r)/2,r);
	lazy_update(k);
}

