	if(l>=r2||r<=l2){
		return {(long long int)1e18,100000};
	}
	if(l2<=l&&r<=r2){
		return seg[id];
	}
	int mid=(l+r)>>1;
	return min(get(id<<1,l,mid,l2,r2),get((id<<1)|1,mid,r,l2,r2));
}
void update2(int id,int l,int r,int l2,int r2,long long int val){
if(l>=r2||r<=l2){
	return;
}	
if(l2<=l&&r<=r2&&r-l==1){
		seg2[id]={val,l2};
		return ;
}
int mid=(l+r)>>1;
update2(id<<1,l,mid,l2,r2,val);
update2((id<<1)|1,mid,r,l2,r2,val);
seg2[id]=min(seg2[id<<1],seg2[(id<<1)|1]);
}
pair<long long int,int>get2(int id,int l,int r,int l2,int r2){
	if(l>=r2||r<=l2){
		return {(long long int)1e18,100000};
	}
	if(l2<=l&&r<=r2){
		return seg2[id];
	}
	int mid=(l+r)>>1;
	return min(get2(id<<1,l,mid,l2,r2),get2((id<<1)|1,mid,r,l2,r2));
}
