inline void input(){
	cin>>n>>e>>d;
	for(int i=0;i<n;i++){
		cin>>a[i];
	}
	for(int i=0;i<4*maxn;i++){
		seg[i]={(long long)1e18,(int)1e9};
		seg2[i]={(long long)1e18,(int)1e9};
	}
	for(int i=0;i<n;i++){
		if(i==0){
			dp[0][i]=a[i];
			dp[1][i]=a[i];
			dp[1][i]+=d;
		}
		else{
		int l=0;
		int r=i;
		while(l!=r-1){
			int mid=(l+r)>>1;
			if(2*(a[i]-a[mid])>=d){
				l=mid;
			}
			else{
				r=mid;
			}
		}
	//	cout<<l<<": "<<r<<" "<<i<<endl;
		pair<long long int,int>p=get(1,0,n,0,l+1);
		//cout<<l<<endl;
		pair<long long int,int>w=get2(1,0,n,r,i);
		dp[0][i]=dp[1][i-1]+(a[i]-a[i-1]);
		dp[1][i]=(long long int)1e18;
		if(2*(a[i]-a[0])>=d){
			dp[1][i]=(dp[0][p.second]+1ll*3*(a[i]-a[p.second]));
		}
		if(2*(a[i]-a[i-1])<d){
			dp[1][i]=min(dp[1][i],(long long)(dp[0][w.second]+d+1ll*2*(a[i]-a[w.second])));
		}
		if(dp[1][i-1]+a[i]-a[i-1]+d<=dp[1][i]){
			dp[1][i]=(long long)(dp[1][i-1]+a[i]-a[i-1]+d);
		}
	}
	//	cout<<dp[0][i]<<" "<<dp[1][i]<<endl;
		//cout<<i<<endl;
		update(1,0,n,i,i+1,(long long)(dp[0][i]+1ll*3*(e-a[i])));
		update2(1,0,n,i,i+1,(long long)(dp[0][i]+1ll*2*(e-a[i])));
	}
	cout<<dp[1][n-1]+(e-a[n-1]);
}

