    for (int i = 0; i < n; ++i) {
        if (a[i + 1] - a[point] > t) {
            res += max(a[i] - a[point], t - a[i] + a[point]) + a[i] - a[point];
            point = i + 1;
        }
        if (i < n - 1)
            res += a[i + 1] - a[i];
    }
    res += e - a[n - 1];
    cout << res;
    return 0;
}

