template <typename T>
struct SegmentTree{
  using F = function<T(T,T)>;
  int n;
  F f;
  T ti;
  vector<T> dat;
  SegmentTree(){};
  SegmentTree(F f,T ti):f(f),ti(ti){}
  void init(int n_){
    n=1;
    while(n<n_) n<<=1;
    dat.assign(n<<1,ti);
  }
  void build(const vector<T> &v){
    int n_=v.size();
    init(n_);
    for(int i=0;i<n_;i++) dat[n+i]=v[i];
    for(int i=n-1;i;i--)
      dat[i]=f(dat[(i<<1)|0],dat[(i<<1)|1]);
  }
  void update(int k,T x){
    dat[k+=n]=x;
    while(k>>=1)
      dat[k]=f(dat[(k<<1)|0],dat[(k<<1)|1]);
  }
  void add(int k,T x){
    dat[k+=n]+=x;
    while(k>>=1)
      dat[k]=f(dat[(k<<1)|0],dat[(k<<1)|1]);
  }
  T query(int a,int b){
    T vl=ti,vr=ti;
    for(int l=a+n,r=b+n;l<r;l>>=1,r>>=1) {
      if(l&1) vl=f(vl,dat[l++]);
      if(r&1) vr=f(dat[--r],vr);
    }
    return f(vl,vr);
  }
};

signed main(){
  ios::sync_with_stdio(false);
  cin.tie(0);

  int n,e,t;
  cin >> n >> e >> t;

  vector<int> a(n,0);
  REP(i,n) cin >> a[i];

  auto f = [](int a, int b){return min(a,b);};
  SegmentTree<int> seg1(f,INF),seg2(f,INF);

  vec tmp(n+1,INF);
  tmp[0] = 0;
  seg1.build(tmp);
  tmp[0] = -2*a[0];
  seg2.build(tmp);

  vec dp(n+1,INF);
  dp[0] = 0;
  FOR(i,1,n+1){
    int id = upper_bound(a.begin(),a.end(),a[i-1]-(t+1)/2) - a.begin() - 1;
    id = min(id,i-1);
    //debug(id);
    dp[i] = min(dp[i],seg1.query(id+1,i)+a[i-1]+t);
    dp[i] = min(dp[i],seg2.query(0,id+1)+a[i-1]*3);
    seg1.update(i,dp[i]-a[i-1]);
    seg2.update(i,dp[i]-a[i-1]-2*a[i]);
    //debug(dp[i]);
  }

  cout << dp[n]+e-a.back() << endl;
  return 0;
}

