    for(int i = 1; i <= n; i++)
        cin >> x[i];

    upd(0, n, 0, 0, -2*x[1]);

    for(int i = 1; i <= n; i++)
    {
        int j = lower_bound(x+1, x+n+1, x[i]-t/2.0)-x;
        if((x[i]-x[j])*2 < t)
            j--;
        dp[i] = min(dp[j]+t, mn(0,n,0,j-1,0)+2*x[i]);
        //cout << i << ' ' << j << ' ' << dp[i] << endl;

        upd(0, n, 0, i, dp[i]-2*x[i+1]);
    }
    cout << dp[n]+e << endl;
    return 0;
}

