void remin(ll &a, ll b){ a = min(a, b); }

void push(int v, ll *t){
	remin(t[v*2+1], t[v]);
	remin(t[v*2+2], t[v]);
}

void upd(int v, int tl, int tr, int l, int r, ll val, ll *t){
	if(tl > r || tr < l) return;
	if(l <= tl && tr <= r){
		remin(t[v], val);
		return;
	}
	push(v, t);
	int tm = (tl+tr)/2;
	upd(v*2+1, tl, tm, l, r, val, t);
	upd(v*2+2, tm+1, tr, l, r, val, t);
}

ll get(int v, int tl, int tr, int p, ll *t){
	if(tl == tr){
		return t[v];
	}else{
		push(v, t);
		int tm = (tl+tr)/2;
		if(p <= tm) return get(v*2+1, tl, tm, p, t);
		else return get(v*2+2, tm+1, tr, p, t);
	}
}

void build(int v, int tl, int tr, ll *t, bool flag){
	t[v] = INF;
	if(tl == tr){
		t[v] = INF;
		if(flag && tl == 0){
			t[v] = 0;
		}
	}else{
		int tm = (tl+tr)/2;
		build(v*2+1, tl, tm, t, flag);
		build(v*2+2, tm+1, tr, t, flag);
	}
}

ll getVal(int id){
	ll ret = get(0, 0, n-1, id, t1) + x[id];
	if(id > 0){
		remin(ret, get(0, 0, n-1, id, t2) + x[id] + 2LL * x[id-1]);
	}
	return ret;
}

int getPos(int l, ll xi){
	int r = n;
	while(r-l>1){
		int m = (l+r)/2;
		ll val = (x[m] - xi) * 2;
		if(val <= t) l = m;
		else r = m;
	}
	return l;
}

void solve(){
	cin >> n >> e >> t;
	rep(i, 0, n) cin >> x[i];
	x[n] = e;	
	n += 1;
	build(0, 0, n-1, t1, true);
	build(0, 0, n-1, t2, false);

	rep(i, 0, n){
		ll cur = getVal(i);
		int pos = getPos(i-1, x[i]);
		//cout << i << " " << cur << endl;
		upd(0, 0, n-1, i+1, min(n-1, pos+1), cur + t - x[i], t1);
		upd(0, 0, n-1, pos+2, n-1, cur - 3 * x[i], t2);
	}

	ll ans = getVal(n - 1);
	cout << ans << endl;
}

int main(){
#ifdef _DEBUG
	freopen("input.txt","r",stdin);
	freopen("output.txt","w",stdout);
#endif

	solve();


	return 0;
}

