void U(int p,int u,int poz,ll val,int node)
{
    if (p == u)
        t[node] = val;
    else
    {
        int m = (p + u) / 2;
        if (poz <= m)
            U(p,m,poz,val,node << 1);
        else
            U(m + 1,u,poz,val,node << 1 | 1);
        t[node] = min(t[node << 1],t[node << 1 | 1]);
    }
}
