        return t[node];
    int m = (p + u) / 2;
    ll ans = 1e18;
    if (l <= m)
        ans = min(ans,query(p,m,l,r,node << 1));
    if (m+1<=r)
        ans = min(ans,query(m+1,u,l,r,node << 1 | 1));
    return ans;
}
