        for(int i = 1, h = 0; i <= n; i++){
                d[i] = inf;
                pref[i] = pref[i - 1];
                while(2 * (a[i] - a[h + 1]) >= T){
                        h += 1;
                }
                if(h > 0){
                        d[i] = 3 * a[i] + pref[h];
                }

                for(int dx = -1; dx <= 1; dx++){
                        int j = h + dx;
                        if(j < 1 || j > i){
                                continue;
                        }
                        long long D = a[i] - a[j];
                        d[i] = min(d[i], d[j - 1] + D + max(D * 2, T) + a[j] - a[j - 1]);
                }
                pref[i] = min(pref[i], - 2 * a[i] - a[i - 1] + d[i - 1]);
        }
        cout << d[n] + E - a[n] << "\n";
}

