long long getmin(int l, int r) {
  l += T;
  r += T;
  long long ret = INF;
  while (l <= r) {
    if (l & 1) ret = std::min(ret, f[l++]);
    if (~r & 1) ret = std::min(ret, f[r--]);
    l >>= 1;
    r >>= 1;
  }
  return ret;
}

