template<class T, int SZ> struct LazySegTree {
    T sum[2*SZ], mn[2*SZ], lazy[2*SZ]; // set SZ to a power of 2

    LazySegTree() {
        memset (sum,0,sizeof sum);
        memset (mn,0,sizeof mn);
        memset (lazy,0,sizeof lazy);
    }

    void push(int ind, int L, int R) {
        sum[ind] += (R-L+1)*lazy[ind];
        mn[ind] += lazy[ind];
        if (L != R) lazy[2*ind] += lazy[ind], lazy[2*ind+1] += lazy[ind];
        lazy[ind] = 0;
    }

    void pull(int ind) {
        sum[ind] = sum[2*ind]+sum[2*ind+1];
        mn[ind] = min(mn[2*ind],mn[2*ind+1]);
    }

    void build() {
        F0Rd(i,SZ) pull(i);
    }

    T qsum(int lo, int hi, int ind = 1, int L = 0, int R = SZ-1) {
        push(ind,L,R);
        if (lo > R || L > hi) return 0;
        if (lo <= L && R <= hi) return sum[ind];

        int M = (L+R)/2;
        return qsum(lo,hi,2*ind,L,M) + qsum(lo,hi,2*ind+1,M+1,R);
    }

    T qmin(int lo, int hi, int ind = 1, int L = 0, int R = SZ-1) {
        push(ind,L,R);
        if (lo > R || L > hi) return INF;
        if (lo <= L && R <= hi) return mn[ind];

        int M = (L+R)/2;
        return min(qmin(lo,hi,2*ind,L,M), qmin(lo,hi,2*ind+1,M+1,R));
    }

    void upd(int lo, int hi, ll inc, int ind = 1, int L = 0, int R = SZ-1) {
        push(ind,L,R);
        if (hi < L || R < lo) return;
        if (lo <= L && R <= hi) {
            lazy[ind] = inc;
            push(ind,L,R);
            return;
        }

        int M = (L+R)/2;
        upd(lo,hi,inc,2*ind,L,M); upd(lo,hi,inc,2*ind+1,M+1,R);
        pull(ind);
    }
};

LazySegTree<ll,1<<17> L[2]; // ctime
int N,E,T,X[MX],y;

void solve(int x) {
    L[0].upd(0,x,X[x]-X[x-1]); 
    L[1].upd(0,x,X[x]-X[x-1]); 

    // y to x-1: ctime+T
    // 0 to y-1: ctime+2*(X[x]-X[i])
    while (2*(X[x]-X[y]) > T) y ++;
    ll a = L[0].qmin(y,x)+T, b = L[1].qmin(0,y-1)+2*X[x];
    ll val = min(a,b);
    // cout << "HI " << x << " " << y << " " << a << " " << b << " " << val << "\n";
    L[0].upd(x+1,x+1,val); L[1].upd(x+1,x+1,val);
}

int main() {
    ios_base::sync_with_stdio(0); cin.tie(0);
    cin >> N;
    cin >> X[N+1] >> T;
    FOR(i,1,N+1) cin >> X[i];
    F0R(i,N+2) L[1].upd(i,i,-2*X[i]);
    FOR(i,1,N+2) solve(i);
    cout << L[0].qmin(N+1,N+1) << "\n";
}

