LL t;
struct UnionFind{
    vector<int> par;
    vector<LL> w;
    UnionFind(int n){
        par = vector<int>(n,-1);
        w=vector<LL>(n,0);
    }
    int root(int x){
        if(par[x]<0){
            return x;
        }else{
            return par[x]=root(par[x]);
        }
    }
    void unite(int x,int y, LL a){
        x=root(x);
        y=root(y);
        if(x!=y){
            if(par[x]<par[y]) swap(x,y);
            par[y] += par[x];
            par[x] = y;
        }
        w[y]=w[y]+w[x]+a;
        return;
    }
    bool same(int x,int y){
        return root(x) == root(y);
    }
    int size(int x){
        return -par[root(x)];
    }
    LL rest(int x){
        return max(0ll,t-w[root(x)]);
    }
};


int main(){
    cin.tie(0);
    ios::sync_with_stdio(false);
