struct segment_tree_min {
	int n; vector<ll> v;
	segment_tree_min(int _n) {
		for (n = 1; n < _n; n *= 2);
		v = vector<ll>(n * 2 - 1, LLONG_MAX / 10);
	}
	void set(int i, ll x) {
		int k = i + n - 1;
		v[k] = x;
		while (k > 0) {
			k = (k - 1) / 2;
			v[k] = min(v[k * 2 + 1], v[k * 2 + 2]);
		}
	}
	ll _get(int i, int j, int k, int l, int r) {
		if (r <= i || j <= l) return LLONG_MAX / 10;
		if (i <= l && r <= j) return v[k];
		ll vl = _get(i, j, k * 2 + 1, l, (l + r) / 2);
		ll vr = _get(i, j, k * 2 + 2, (l + r) / 2, r);
		return min(vl, vr);
	}
	ll get(int i, int j) { return _get(i, j, 0, 0, n); }
};

int main() {
	int N, E, T;
	cin >> N >> E >> T;
	vector<int> x(N);
	rep(i, N) scanf("%d", &x[i]);
	vector<ll> dp(N + 1);
	dp[0] = 0;
	segment_tree_min st1(N + 1), st2(N + 1);
	st1.set(0, 0); st2.set(0, - x[0] * 2);
	for (int i = 1; i <= N; i++) {
		ll ans = LLONG_MAX / 10;
		int lb = -1, ub = i - 1;
		while (ub - lb > 1) {
			int mid = (lb + ub) / 2;
			if ((x[i - 1] - x[mid]) * 2 <= T) ub = mid;
			else lb = mid;
		}
		int l = ub;
		ans = min(ans, st1.get(l, i) + T);
		ans = min(ans, st2.get(0, l) + x[i - 1] * 2);
		dp[i] = ans;
		st1.set(i, ans);
		if (i < N) st2.set(i, ans - x[i] * 2);
	}
	cout << dp[N] + E << endl;
	return 0;
	for (int i = 0; i <= N; i++)
		for (int j = i + 1; j <= N; j++)
			dp[j] = min(dp[j], dp[i] + max(T, (x[j - 1] - x[i]) * 2));
	cout << dp[N] + E << endl;
}

