				f[x]=min(f[x],f[now]+tmp);
			}
			else f[x]=min(f[x],f[now]+2ll*(pos[x]-pos[now+1]));
		}
		f[now+1]=min(f[now+1],f[now]+wait);
	}
	cout<<f[n]+e;
