using namespace std;
const int N = 1e5 + 5;
const int SN = 1 << 18;
const long long inf = 1e16;
int n , e , t;
int x[N];
long long cost(int l , int r){
    long long res = 0;
    res += x[r] - x[l];
    res += x[r] - x[l];
    res = max(res , 1LL * t);
    res += x[r] - x[l];
    return res;
}
long long dp[N];
long long suf[N];
long long dp2[N];
long long segtree[SN];
void build(int l , int r , int node){
    segtree[node] = inf;
    if(l < r){
        int mid = l + r >> 1;
        build(l , mid , node + node);
        build(mid + 1 , r , node + node + 1);
    }
}
void update(int l , int r , int node , int idx , long long value){
    if(l == r){
        segtree[node] = value;
    }
    else{
        int mid = l + r >> 1;
        if(idx <= mid){
            update(l , mid , node + node , idx , value);
        }
        else{
            update(mid + 1 , r , node + node + 1 , idx , value);
        }
        segtree[node] = min(segtree[node + node] , segtree[node + node + 1]);
    }
}
bool check(int i , int j){
    return 2LL * (x[j] - x[i]) >= t;
}
long long query(int l , int r , int node , int ql , int qr){
    if(l > qr || r < ql){
        return inf;
    }
    if(l >= ql && r <= qr){
        return segtree[node];
    }
    int mid = l + r >> 1;
    return min(query(l , mid , node + node , ql , qr) , query(mid + 1 , r , node + node + 1 , ql , qr));
}
long long getmin(int l , int r){
    return query(1 , n , 1 , l , r);
}
int main(){
    scanf("%d %d %d" , &n , &e , &t);
    for(int i = 1 ; i <= n ; ++i){
        scanf("%d" , x + i);
    }
    build(1 , n , 1);
    x[0] = 0;
    x[n + 1] = e;
    dp[n + 1] = x[n + 1] - x[n];
    suf[n + 1] = inf;
    for(int pos = n ; pos >= 1 ; --pos){
        dp[pos] = inf;
        int j;
        if(!check(pos , n)){
            j = n + 1;
        }
        else{
            int l = pos;
            int r = n;
            while(l < r){
                int mid = l + r >> 1;
                if(check(pos , mid)){
                    r = mid;
                }
                else{
                    l = mid + 1;
                }
            }
            j = l;
        }
        dp2[pos] = t + dp[pos + 1] + x[pos];
        update(1 , n , 1 , pos , dp2[pos]);
        dp[pos] = min(dp[pos] , getmin(pos , j - 1) - x[pos]);
        dp[pos] = min(dp[pos] , suf[j] - 3LL * x[pos]);
        dp[pos] += x[pos] - x[pos - 1];
        suf[pos] = min(suf[pos + 1] , 3LL * x[pos] + dp[pos + 1]);
    }
    printf("%lld\n" , dp[1]);
}

