template<typename T>
struct SegmentTree {
	int n;
	T unit;
	vector<T>dat;
	function<T(T, T)> func;
	SegmentTree(const int N, T _unit, function<T(T, T)> _func)
		:unit(_unit), func(_func) {
		n = 1;
		//ç°¡å˜ã®ãŸã‚ã€è¦ç´ æ•°ã‚’2ã®ã¹ãä¹—ã«
		while (n < N)n *= 2;
		dat.assign(2 * n, unit);
	}

	void update(int k, T a) {
		k += n - 1;//è‘‰ã®ç¯€ç‚¹
		dat[k] = a;
		//ä¸Šã‚ŠãªãŒã‚‰æ›´æ–°
		while (k > 0) {
			k = (k - 1) / 2;
			dat[k] = func(dat[k * 2 + 1], dat[k * 2 + 2]);
		}
	}
	T _query(int a, int b, int k, int l, int r) {
		//[a,b)ã¨[l,r)ãŒäº¤å·®ã—ã¦ã„ãªã‘ã‚Œã°ã€funcã«å½±éŸ¿ã‚’ä¸Žãˆãªã„å€¤ã‚’è¿”ã™
		if (r <= a || b <= l)return unit;
		//[a,b)ãŒ[l,r)ã‚’å®Œå…¨ã«å«ã‚“ã§ã„ã‚Œã°ã€ã“ã®ç¯€ç‚¹ã®å€¤
		if (a <= l && r <= b)return dat[k];
		else {
			T vl = _query(a, b, k * 2 + 1, l, (l + r) / 2);
			T vr = _query(a, b, k * 2 + 2, (l + r) / 2, r);
			return func(vl, vr);
		}
	}
	//[a,b)
	T query(int a, int b) {
		return _query(a, b, 0, 0, n);
	}
};



ll dp[100010];
void solve() {
	int N, E, T; cin >> N >> E >> T;
	vector<int>x(N + 1);
	REP(i, N) {
		cin >> x[i];
	}
	x[N] = E;
	dp[N] = 0;
	REP(i, N)dp[i] = LINF;
	auto f = [](int a, int b) {return min(a,b); };
	SegmentTree<int>seg(N+1,LINF,f);

	REPR(i, N) {
		seg.update(i, x[i + 1] + 2*x[i] + dp[i + 1]);
		int ok = i, ng = N;
		while (abs(ok - ng) > 1) {
			int mid = (ok + ng) / 2;
			if (T >= 2 * (x[mid] - x[i]))ok = mid;
			else ng = mid;
		}
		int res = T + x[ok + 1] - x[i] + dp[ok + 1];
		if (ok < N - 1) {
			int tmp = seg.query(ok + 1, N);
			res = min(res, tmp - 3 * x[i]);
		}
		dp[i] = res;

	}
	print(x[0] + dp[0]);
}



