			if((x.at(i+1)-x.at(i))*2-T>=T) ans+=T;
			else{
				int k=i;
				for(int j=i+1;j<n;++j){
					if((x.at(j)-x.at(i))*2-T<T){
						k=j;
						b.at(j)=true;
					}
					else{
						if((x.at(j)-x.at(j-1))*2-T-(T-(x.at(j-1)-x.at(i))*2)<(x.at(j)-x.at(j-1))*2-T){
							k=j;
							b.at(j)=true;
						}
						break;
					}
				}
				ans+=(x.at(k)-x.at(i))*2;
			}
			ans+=x.at(i+1)-x.at(i);
		}
	}
	cout << ans << endl;
}

