void add(int v, int l, int r, int i, ll val)
{
    if(r - l == 1)
    {
        seg[v] = val;
        return ;
    }
    int mid = (l + r) / 2;
    if(i < mid)
        add(v * 2, l, mid, i, val);
    else
        add(v * 2 + 1, mid, r, i, val);
    seg[v] = min(seg[v * 2], seg[v * 2 + 1]);
}

ll query(int v, int l, int r, int b, int e)
{
    if(e <= l || r <= b)
        return inf;
    if(b <= l && r <= e)
        return seg[v];
    int mid = (l + r) / 2;
    return min(query(v * 2, l, mid, b, e), query(v * 2 + 1, mid, r, b, e));
}

