#define modulo 1000000007
#define mod(mod_x) ((((long long)mod_x+modulo))%modulo)
#define Inf 1000000000000000000

template <typename T,typename F>
struct SWAG{
	F func;
	T init_value;
	vector<pair<T,T>> X,Y;
	SWAG(F f,T iv):func(f){
		init_value = iv;
	}

	void push_front(T x){
		if(X.empty())X.push_back({x,x});
		else X.push_back({x,func(x,X.back().second)});
	}

	void push_back(T x){
		if(Y.empty())Y.push_back({x,x});
		else Y.push_back({x,func(Y.back().second,x)});
	}

	void pop_front(){
		if(X.empty()){
			int n = Y.size();
			vector<T> t;
			for(int i=0;i<n/2;i++){
				t.push_back(Y.back().first);
				Y.pop_back();
			}
			while(!Y.empty()){
				push_front(Y.back().first);
				Y.pop_back();
			}
			while(!t.empty()){
				push_back(t.back());
				t.pop_back();
			}
			if(!X.empty())X.pop_back();
		}
		else{
			X.pop_back();
		}
	}

	void pop_back(){
		if(Y.empty()){
			int n = X.size();
			stack<T> t;
			for(int i=0;i<n/2;i++){
				t.push_back(X.back().first);
				X.pop_back();
			}
			while(!X.empty()){
				push_back(X.back().first);
				X.pop_back();
			}
			while(!t.empty()){
				push_front(t.back());
				t.pop_back();
			}
			if(!Y.empty())Y.pop_back();
		}
		else{
			Y.pop_back();
		}
	}

	T get(){
		T ret = init_value;
		if(!X.empty())ret = func(ret,X.back().second);
		if(!Y.empty())ret = func(ret,Y.back().second);
		return ret;
	}

	T front(){
		if(!X.empty())return X.back().first;
		if(!Y.empty())return Y[0].first;
		return init_value;
	}

	T back(){
		if(!Y.empty())return Y.back().first;
		if(!X.empty())return X[0].first;
		return init_value;
	}

};

int main(){

	int N;
	cin>>N;

	long long E,T;
	cin>>E>>T;

	vector<long long> x(N);
	for(int i=0;i<N;i++)cin>>x[i];
	x.push_back(E);

	vector<long long> dp(x.size(),Inf);
	dp[0] = x[0];

	auto f = [](long long a,long long b){
		return min(a,b);
	};

	SWAG<long long,decltype(f)> minT(f,Inf),minX(f,Inf);
	minT.push_back(dp[0]-x[0]);
	int now = 0;

	for(int i=1;i<dp.size();i++){
		while(now!=i&&(x[i-1]-x[now])*2>T){
			minT.pop_front();
			minX.push_back(dp[now]-3*x[now]);
			now++;
		}
		dp[i] = min(minT.get()+T+x[i],minX.get()+x[i]+x[i-1]*2);
		minT.push_back(dp[i]-x[i]);
	}

	cout<<dp.back()<<endl;

	return 0;
}

