    X[N+1] = E;
    dp[0] = X[1];
    seg1.update(0,dp[0]-X[1]);
    seg2.update(0,dp[0]-3*X[1]);
    for(int i=1;i<=N;i++){
        dp[i] = dp[i-1]+X[i+1]-X[i]+T;
