class starry_sky_tree{
public:
  static const int MAX_SIZE = 1 << 17; //segment tree ã®ã‚µã‚¤ã‚ºã€‚ 2^17 â‰’ 1.3 * 10^5
  using Int = long long;
  Int segMin[2 * MAX_SIZE - 1], segAdd[2 * MAX_SIZE - 1];

  starry_sky_tree(){
    fill(segMin, segMin + 2*MAX_SIZE-1, 0);
    fill(segAdd, segAdd + 2*MAX_SIZE-1, 0);
  }

  //åŒºé–“[a, b)ã«å€¤xã‚’åŠ ç®—ã™ã‚‹.
  void add(int a, int b, Int x, int k = 0, int l = 0, int r = MAX_SIZE)
  {
    if (r <= a || b <= l) return; //ã‚‚ã—äº¤å·®ã—ãªã„åŒºé–“ã§ã‚ã‚Œã°çµ‚ãˆã‚‹.

    if (a <= l && r <= b){ //ã‚‚ã—ä»Šã¿ã¦ã„ã‚‹åŒºé–“[l, r)ãŒ[a, b)ã«å®Œå…¨ã«å†…åŒ…ã•ã‚Œã¦ã„ã‚Œã°
      segAdd[k] += x;  //åŒºé–“[l, r)ã«kã‚’åŠ ç®—ã™ã‚‹.
      return;
    }

    add(a, b, x, k * 2 + 1, l, (l + r) / 2); //å­ã®åŒºé–“ã«(å¿…è¦ãŒã‚ã‚Œã°)xã‚’åŠ ç®—ã™ã‚‹.
    add(a, b, x, k * 2 + 2, (l + r) / 2, r); //ã€ƒ

    //è¦ªã®åŒºé–“ã®æœ€å°å€¤ã¯, å­ã®åŒºé–“ã®æœ€å°å€¤ + è‡ªåˆ†ã«ä¸€æ§˜ã«åŠ ç®—ã•ã‚Œã¦ã„ã‚‹å€¤ ã§ã‚ã‚‹.ä¸€æ§˜ã«åŠ ç®—ã•ã‚Œã‚‹å€¤ã¯æ›´æ–°ã—ãªãã¦è‰¯ã„.
    segMin[k] = min(segMin[k * 2 + 1] + segAdd[k * 2 + 1], segMin[k * 2 + 2] + segAdd[k * 2 + 2]);
  }

  Int getMin(int a, int b, int k = 0, int l = 0, int r = MAX_SIZE)
  {
    if (r <= a || b <= l) return (1ll<<57);

    if (a <= l && r <= b) return (segMin[k] + segAdd[k]); //å®Œå…¨ã«å†…åŒ…ã•ã‚Œã¦ã„ã‚Œã°,ãã®åŒºé–“ã®æœ€å°å€¤ã‚’è¿”ã™.

    Int left = getMin(a, b, k * 2 + 1, l, (l + r) / 2); //å­ã®åŒºé–“ã®æœ€å°å€¤ã‚’æ±‚ã‚ã‚‹.
    Int right = getMin(a, b, k * 2 + 2, (l + r) / 2, r); //å­ã®åŒºé–“ã®æœ€å°å€¤ã‚’æ±‚ã‚ã‚‹

    return (min(left, right) + segAdd[k]); //è¦ªã®åŒºé–“ã®æœ€å°å€¤ã¯, å­ã®åŒºé–“ã®æœ€å°å€¤ + è‡ªåˆ†ã«ä¸€æ§˜ã«åŠ ç®—ã•ã‚Œã¦ã„ã‚‹å€¤ ã§ã‚ã‚‹ (å¤§åˆ‡ãªã®ã§2å›žæ›¸ãã¾ã—ãŸ!!)

  }
};

