ll query(int id,int a, int b, int k, int l, int r) {
	if (r <= a || b <= l)return INF;
	if (a <= l && r <= b)return dat[id][k];
	else {
		ll vl = query(id, a, b, k * 2 + 1, l, (l + r) / 2);
		ll vr = query(id, a, b, k * 2 + 2, (l + r) / 2, r);
		return min(vl, vr);
	}
}
