int n, t, e;
vi x;
ll dp[2222][2222];

ll solve(int p, int col)
{
	if (dp[p][col] >= 0) return dp[p][col];
	ll res = 0;
	if (p == n - 1 && col == n)
	{
		res = e - x[n - 1];
	}
	else if (p == n - 1)
	{
		ll d = x[p] - x[col];
		ll tmp;
		if (d * 2 <= t) tmp = t;
		else tmp = d * 2;
		res = tmp + solve(n - 1, n);
	}
	else
	{
		ll dist = x[p + 1] - x[p];
		ll tmp;
		ll d = x[p] - x[col];
		if (d * 2 <= t) tmp = t;
		else tmp = d * 2;
		res = dist + min(solve(p + 1, col), solve(p + 1, p + 1) + tmp);
	}
	return dp[p][col] = res;
}

