bool Cmp(const int&i,const int&j){
	if(!h[i].size())return 0;
	if(!h[j].size())return 1;
	return h[i][0]<h[j][0];
}
inline int Pow(int a,int m){int s=1;for(;m;m>>=1)m&1?s=1ll*s*a%M:0,a=1ll*a*a%M;return s;}
void InC(){
	f[0]=1;
	for(int i=1;i<=n;i++)f[i]=1ll*f[i-1]*i%M;
	invf[n]=Pow(f[n],M-2);
	for(int i=n;i;i--)invf[i-1]=1ll*invf[i]*i%M;
}
inline int C(int n,int m){return 1ll*f[n]*invf[m]%M*invf[n-m]%M;}
int main(){
