	  if(h[t[i]][0]+h[t[k]][0]<=y)d=i;
	}
	ans=1;sum=0;
	for(int i=1;i<=d;i++)sum+=c[t[i]]+1;
	for(int i=1;i<=d;i++)
	  ans=1ll*ans*C(sum,c[t[i]]+1)%M,
