const ll INF = 1e18;

template<typename T>
struct SegmentTree{
    const T INF = 1e18;
    int n, width;
    vector<T> dat;

    void init(int x){
        n = x;
        width = 1;
        while (width < n) width *= 2;
        dat.assign(2*width-1, INF);
    }

    void update(int i, T x){
        i += width - 1;
        dat[i] = x;
        while (i > 0){
            i = (i - 1) / 2;
            dat[i] = min(dat[i*2+1], dat[i*2+2]);
        }
    }

    T query(int a, int b, int k, int l, int r){
        if (r <= a || b <= l) return INF;
        if (a <= l && r <= b) return dat[k];
        T vl = query(a, b, k*2+1, l, (l+r)/2);
        T vr = query(a, b, k*2+2, (l+r)/2, r);
        return min(vl, vr);
    }

    // [l, r)
    T query(int l, int r){
        return query(l, r, 0, 0, width);
    }
};

int main() {
    ll n, e, t;
    cin >> n >> e >> t;
    VL x(n);
    REP(i,n) x[i] = in();
    VL dp(n + 1, INF);
    dp[0] = 0;
    SegmentTree<ll> seg1, seg2;
    seg1.init(n);
    seg2.init(n);
    seg1.update(0, 0);
    seg2.update(0, -2 * x[0]);
    int i = 0;
    REP(j,n){
        while (2 * (x[j] - x[i]) > t) i++;
        chmin(dp[j + 1], seg1.query(i, j + 1) + t);
        chmin(dp[j + 1], seg2.query(0, i) + 2 * x[j]);
        seg1.update(j + 1, dp[j + 1]);
        seg2.update(j + 1, dp[j + 1] - 2 * x[j + 1]);
    }
    // REP(i,n+1) cout << dp[i] << endl;
    ll ans = e + dp[n];
    cout << ans << endl;
    return 0;
}

