    for (int i=1; i<=N; i++) {

	while (2LL * (A[i] - A[mid]) > T) mid++;
	cst = 1LL << 60;

	if (0 < mid-1) amin(cst, X1.min_v(0, mid-1) + 3LL * A[i]);
	if (mid-1 < i) amin(cst, X2.min_v(max(0, mid-1), i) + T + A[i]);
	amin(cst, X2.min_v(i-1, i) + T + A[i]);

	X1.modify(i, cst - 2LL * A[i+1] - A[i]);
	X2.modify(i, cst - A[i]);

//	eprintf("%d %lld %d\n", i, cst, mid);
    }

    LL ans = cst + E - A[N];
    printf("%lld\n", ans);

