            else left=mid;
        }
        chmin(dp[i],T+dp[right]);
        chmin(dp[i],seg.query(0,right));

        if(i!=N) seg.update(0,i,2*(A[i]-A[i-1]));

        while(last<N){
            if(seg.query(last,last+1)>T){
                seg.update(last,last+1,dp[last]);
