void upd(int v, ll val, int type)
{
	v += n - 1;
	t[type][v] = min(t[type][v], val);
	while(v != 1)
	{
		v >>= 1;
		t[type][v] = min(t[type][v << 1], t[type][v << 1 | 1]);
	}
}

ll get(int l, int r, int type)
{
	l += n - 1;
	r += n - 1;
	ll res = INF;
	while(l <= r)
	{
		if (l & 1)
			res = min(res, t[type][l++]);
		if (!(r & 1))
			res = min(res, t[type][r--]);
		l >>= 1, r >>= 1;			
	}
	return res;
}

