      ok = min(ok, f[q[l]] - 2 * x[q[l] + 1]);
      l++;
    }
    f[i] = min(f[i], f[q[l]] + T);
    f[i] = min(f[i], ok + 2 * x[i]);
    q[++r] = i;
  }
  ans = f[n] + E;
  printf("%lld\n", ans);
  return 0;
}

