    ll x[100005];
    x[0] = 0;
    for(int i = 1; i <= n; i++){
        cin >> x[i];
        x[i] *= 2;
    }
    x[n + 1] = e;
    SegmentTree<P> seg(n + 2, [](P a, P b){return min(a, b);}, P(INF, INF));
    ll dp[100005];
    for(int i = 0; i <= n; i++) dp[i] = INF;
    for(int i = 0; i <= n; i++){
        if(i == 0) dp[0] = 0;
        int l = upper_bound(x, x + n + 2, x[i] - t / 2) - x;
        if(l - 1 > 0){
            int j = seg.query(0, l - 1).second;
            dp[i] = min(dp[i], dp[j] + (x[i] - x[j]) * 3 - (x[j + 1] - x[j]) * 2);
        }
        seg.update(i, P(dp[i] + (e - x[i]) * 3 - (x[i + 1] - x[i]) * 2, i));
        int r = upper_bound(x, x + n + 1, x[i + 1] + t / 2) - x - 1;
        dp[r] = min(dp[r], dp[i] + t + (x[r] - x[i]));
    }
    cout << (dp[n] + (e - x[n])) / 2 << endl;
}

