void add(int id, int lq, int rq, int x, int l, int r){
	if (rq <= l || r <= lq) return;
	if (lq <= l && r <= rq){
		seg[id] = x;
		return;
	}
	int md = (l + r) >> 1;
	add(id << 1, lq, rq, x, l, md);
	add(id << 1 | 1, lq, rq, x, md, r);
	seg[id] = min(seg[id << 1], seg[id << 1 | 1]);
}

int get(int id, int lq, int rq, int l, int r){
	if (rq <= l || r <= lq) return INF;
	if (lq <= l && r <= rq) return seg[id];
	int md = (l + r) >> 1;
	return min(get(id << 1, lq, rq, l, md), get(id << 1 | 1, lq, rq, md, r));
}

