ll solve(int i, int lastp) {
  if(m[i][lastp] != -1) return m[i][lastp];
  ll tmp;
  if(i == N) {
    if(lastp == 0)
      tmp = T;
    else
      tmp = max(T-2*(xs[i]-xs[lastp]), 0LL) + 2*(xs[i]-xs[lastp]);
    return m[i][lastp] = tmp;
  }
  if(lastp == 0)
    tmp = T + (xs[i+1]-xs[i]) + solve(i+1,0);
  else
    tmp = max(T-2*(xs[i]-xs[lastp]) , 0LL) + 2*(xs[i]-xs[lastp]) + solve(i+1, 0) + (xs[i+1]-xs[i]);

  tmp = min(tmp, solve(i+1, lastp == 0 ? i : lastp)+(xs[i+1]-xs[i]));

  return m[i][lastp] = tmp;
}

