        c = getchar();
    while (isdigit(c)) {
        x = (x << 3) + (x << 1) + c - '0';
        c = getchar();
    }
    return x;
}

void pushp(int x) {
    while (!p.empty() && f[x] + v[x + 1] - d[x + 1] < f[p.back()] + v[p.back() + 1] - d[p.back() + 1])
        p.pop_back();
    p.push_back(x);
}

void pushq(int x) {
    while (!q.empty() && f[x] + v[x + 1] - d[x + 1] * 3 < f[q.back()] + v[q.back() + 1] - d[q.back() + 1] * 3)
        q.pop_back();
    q.push_back(x);
}

